json_data_schema:
{
    "description": "File Input",
    "type": "object",
    "required": [
        "label",
        "directory",
        "type"
    ],
    "properties": {
        "label": {
            "type": "string"
        },
        "directory": {
            "title": "Directorio",
            "type": "string"
        },
        "out_format": {
            "title": "Formato Recibido",
            "type": "string",
            "enum": [
                "json",
                "hl7",
                "raw"
            ],
            "enumNames": [
                "JSON",
                "HL7",
                "RAW"
            ]
        },
        
        "type": {
            "title": "Tipo",
            "type": "string",
            "enum": [
                "file",
                "file-watch",
                "aws-s3"
            ],
            "enumNames": [
                "File",
                "FileWatch",
                "aws-s3"
            ]
        }
    },
    "dependencies": {
        "type": {
            "oneOf": [
                {
                    "properties": {
                        "type": {
                            "enum": [
                                "file"
                            ]
                        },
                        "query_params": {
                            "title": "Parámetros extra",
                            "type": "object",
                            "properties": {
                                "exclude": {
                                    "type": "string",
                                    "title": "Patron Regex para EXCLUIR archivos en la lectura."
                                },
                                "include": {
                                    "type": "string",
                                    "title": "Patron Regex para INCLUIR archivos en la lectura."
                                },
                                "schedule": {
                                    "type": "string",
                                    "title": "Schedule"
                                },
                                "bufferSize": {
                                    "type": "number",
                                    "title": "Tamaño de escritura del buffer (Bytes)"
                                },
                                "readLockMinAge": {
                                    "type": "number",
                                    "title": "Edad del archivo minima (segundos)"
                                },
                                "delete": {
                                    "type": "string",
                                    "title": "Eliminar el archivo original despues del procesamiento",
                                    "enum": [
                                        "true",
                                        "false"
                                    ],
                                    "enumNames": [
                                        "Si",
                                        "No"
                                    ]
                                },
                                "recursive": {
                                    "type": "string",
                                    "title": "Habilitar Busqueda recusriva.",
                                    "enum": [
                                        "true",
                                        "false"
                                    ],
                                    "enumNames": [
                                        "Si",
                                        "No"
                                    ]
                                },
                                "localWorkDirectory": {
                                    "type": "string",
                                    "title": "Guardar en memoria o directamente en el directorio (Solo para archivos locales)",
                                    "enum": [
                                        "true",
                                        "false"
                                    ],
                                    "enumNames": [
                                        "Si",
                                        "No"
                                    ]
                                },
                                "allowNullBody": {
                                    "type": "string",
                                    "title": "Permitir que el archivo este vacio",
                                    "enum": [
                                        "true",
                                        "false"
                                    ],
                                    "enumNames": [
                                        "Si",
                                        "No"
                                    ]
                                },
                                "fileExist": {
                                    "type": "string",
                                    "title": "¿ Que hacer si el archivo existe ?",
                                    "enum": [
                                        "Override",
                                        "Append",
                                        "Fail"
                                    ],
                                    "enumNames": [
                                        "Sobreescribir",
                                        "Adjuntar",
                                        "Marcar como error"
                                    ]
                                }
                            }
                        }
                    }
                },
                {
                    "properties": {
                        "type": {
                            "enum": [
                                "file-watch"
                            ]
                        },
                        "query_params": {
                            "title": "Parámetros extra",
                            "type": "object",
                            "properties": {
                                "events": {
                                    "type": "string",
                                    "title": "Eventos del directorio a escuchar.",
                                    "enum": [
                                        "CREATE",
                                        "MODIFY",
                                        "DELETE"
                                    ],
                                    "enumNames": [
                                        "Creación de ficheros/directorios",
                                        "Modificación de ficheros/directorios",
                                        "Eliminación de ficheros/directorios"
                                    ]
                                },
                                "recursive": {
                                    "type": "string",
                                    "title": "Comprobar recursivamente los directorios",
                                    "enum": [
                                        "true",
                                        "false"
                                    ],
                                    "enumNames": [
                                        "Si",
                                        "No"
                                    ]
                                }
                            }
                        }
                    }
                },
                {
                    "properties": {
                        "type": {
                            "enum": [
                                "aws-s3"
                            ]
                        },
                        "query_params": {
                            "title": "Parámetros extra",
                            "type": "array",
                            "items": {
                                "type": "object",
                                "required": [
                                    "code",
                                    "value"
                                ],
                                "properties": {
                                    "code": {
                                        "title": "Clave",
                                        "type": "string"
                                    },
                                    "value": {
                                        "title": "Valor",
                                        "type": "string"
                                    }
                                }
                            }
                        }
                    }
                }
            ]
        }
    }
}
