json_ui_schema:
{
    "type": {
        "ui:columnSize": "4"
    },
    "allowNullBody": {
        "ui:columnSize": "4"
    },
    "label": {
        "ui:columnSize": "4"
    },
    "directory": {
        "ui:columnSize": "4"
    },
    "query_params": {
        "ui:columnSize": "12",
        "columns": [
            {
                "title": "File Name",
                "dataIndex": "fileName"
            },
            {
                "title": "Create additional file to mark as done ( done-${file:name} ||| ${file:name}.done )",
                "dataIndex": "doneFileName"
            },
