json_data_schema:
{
    "description": "File Output",
    "type": "object",
    "required": [
        "label",
        "directory"
    ],
    "properties": {
        "label": {
            "type": "string"
        },
        "directory": {
            "title": "Directorio",
            "type": "string"
        },
        "query_params": {
            "title": "Parámetros extra",
            "type": "object",
            "properties": {
                "fileName": {
                    "type": "string",
                    "title": "Nombre del archivo en destino (Si no se introduce sera un ID autogenerado)"
                },
                "doneFileName": {
                    "type": "string",
                    "title": "Crear un fichero adiciónal con la nomenclatura especifica para indicar su estado ( done-${file:name} ||| ${file:name}.done )"
                },
                "fileExist": {
                    "type": "string",
                    "title": " ¿ Que hacer si el archivo existe ?",
                    "enum": [
                        "Override",
                        "Append",
                        "Fail"
                    ],
                    "enumNames": [
                        "Sobreescribir",
                        "Adjuntar",
                        "Marcar como error"
                    ]
                },
                "flatten": {
                    "type": "string",
                    "title": "Seguir la estructura antigua de directorios o volcar directamente los archivos encontrados en la carpeta",
                    "enum": [
                        "true",
                        "false"
                    ],
                    "enumNames": [
                        "True",
                        "False"
                    ]
                },
                "items": {
                    "title": "Extra Configs",
                    "type": "array",
                    "items": {
                        "type": "object",
                        "required": [
                            "code",
                            "value"
                        ],
                        "properties": {
                            "code": {
                                "title": "Clave",
                                "type": "string"
                            },
                            "value": {
                                "title": "Valor",
                                "type": "string"
                            }
                        }
                    }
                }
            }
        }
    }
}
