json_data_schema:
{
    "description": "RabbitMQ Input",
    "type": "object",
    "required": [
        "label",
        "hostname",
        "port"
    ],
    "properties": {
        "label": {
            "type": "string"
        },
        "hostname": {
            "title": "Host",
            "type": "string"
        },
        "port": {
            "title": "Puerto",
            "type": "number"
        },

        "query_params": {
            "title": "Parámetros",
            "type": "object",
            "properties": {
                "username": {
                    "title": "usuario",
                    "type": "string"
                },
                "password": {
                    "title": "contraseña",
                    "type": "string"
                },
                "queue": {
                    "title": "Nombre de la cola",
                    "type": "string"
                },
                "exchange_declare":{
                    "title": "Exchange Declaration",
                    "type": "boolean",
                    "default": false
                },
                "prefetch_options":{
                    "title": "Prefetch Options",
                    "type": "boolean",
                    "default": true
                },
                "autoAck": {
                    "title": "autoACK",
                    "type": "boolean",
                    "default": true
                },
                "threadPoolSize": {
                    "title": "threadPoolSize",
                    "type": "number",
                    "default": 10
                },
                "autoDelete": {
                    "title": "auto delete Queue",
                    "type": "boolean",
                    "default": true
                }
            }
        },
        "prefetch_params": {
            "title": "Prefetch options",
            "type": "object",
            "properties": {
            "prefetchGlobal": {
                "type": "string",
                "title": "prefetchGlobal",
                "enum": [
                    "true",
                    "false"
                ],
                "enumNames": [
                    "Si",
                    "No"
                ],
                "default": "true"
            },
            "prefetchCount": {
                "title": "prefetchCount",
                "type": "number",
                "default": 10
            },
            "prefetchSize": {
                "title": "prefetchSize",
                "type": "number",
                "default": 10
            }
            }
        },
        "exchange_declare":{
            "title": "Skip exchange declare",
            "type": "object",
            "properties": {
                "exchange": {
                    "title": "Exchange",
                    "type": "string"
                },
                "skipExchangeDeclare": {
                    "title": "Skip exchange declare",
                    "type": "boolean",
                    "default": true
                },
                "skipQueueBind": {
                    "title": "Skip queue bind",
                    "type": "boolean",
                    "default": true
                }
            }
        },
          "extra_query_params": {
            "title": "Parámetros Extra",
            "type": "array",
            "items": {
                "type": "object",
                "required": [
                    "code",
                    "value"
                ],
                "properties": {
                    "code": {
                        "title": "Clave",
                        "type": "string"
                    },
                    "value": {
                        "title": "Valor",
                        "type": "string"
                    }
                }
            }
        }
    }
}
