json_data_schema:
{
    "description": "TCP/MLLP Input",
    "type": "object",
    "required": [
        "label",
        "hostname",
        "port"
    ],
    "properties": {
        "label": {
            "type": "string"
        },
        "out_format": {
            "title": "Formato Recibido",
            "type": "string",
            "enum": [
                "json",
                "hl7",
                "raw"
            ],
            "enumNames": [
                "JSON",
                "HL7",
                "RAW"
            ]
        },
        "protocol": {
            "title": "Protocolo",
            "type": "string",
            "enum": [
                "netty:tcp",
                "mllp"
            ],
            "enumNames": [
                "TCP Netty",
                "MLLP"
            ]
        },
        "hostname": {
            "title": "Host",
            "type": "string"
        },
        "port": {
            "title": "Puerto",
            "type": "number"
        },
        "mllp_query_params": {
            "title": "Opciones",
            "type": "object",
            "properties": {
                "autoAck": {
                    "type": "boolean",
                    "default": true,
                    "title": "Auto Ack"
                },
                "charsetName": {
                    "type": "string",
                    "title": "Charset"
                },
                "requireEndOfData": {
                    "type": "boolean",
                    "default": true,
                    "title": "Requerir EndOfData"
                },
                "stringPayload": {
                    "type": "boolean",
                    "default": true,
                    "title": "Convertir Payload String"
                },
                "validatePayload": {
                    "type": "boolean",
                    "default": false,
                    "title": "Validar Payload HL7"
                },
                "acceptTimeout": {
                    "type": "number",
                    "default": 60000,
                    "title": "Timeout espera conexión (servidor)"
                },
                "backlog": {
                    "type": "number",
                    "default": 5,
                    "title": "Tamaño de la cola de conexiones"
                }
            },
            "additionalProperties": true
        },
        "netty_query_params": {
            "title": "Opciones",
            "type": "object",
            "properties": {
                "disconnect": {
                    "type": "boolean",
                    "default": false,
                    "title": "Desconectar el socket cada uso"
                },
                "keepAlive": {
                    "type": "boolean",
                    "default": true,
                    "title": "Mantener viva la conexión"
                },
                "sync": {
                    "type": "boolean",
                    "default": true,
                    "title": "Sincronizar (OneWay)"
                },
                "tcpNoDelay": {
                    "type": "boolean",
                    "default": true,
                    "title": "No Delay"
                }
            },
            "additionalProperties": true
        }
    }
}
